actionConfiguration.body:
() => {
  let confirm = appsmith.store.confirm;
  confirm.response = true;
  storeValue('confirm', confirm);
  closeModal('mdl_confirm');
  let title = confirm.title;
  switch (title) {
    case 'WARNING':
      utils.resetCart();
      break;
    case 'CHECKOUT':
      navigateTo('Checkout', 'SAME_WINDOW');
      break;
    case 'RESET FILTERS':
      storeValue('search', '');
      storeValue('filter', '');
      resetWidget('Select1');
      break;
    default:
  }
  return confirm;
}

